#include <iostream>
using namespace std;

#define charToInt(c) (c-'0')

string sumOfDigits(string number)
{
    int sum = 0;
    for (int i=0; i<number.length(); ++i) {
        sum += charToInt(number[i]);
    }
    return to_string(sum);
}

int main()
{
    // 
    string n;
    int count = 0;
    cin >> n;

    while (n.length() >= 2)
    {
        count++;
        n = sumOfDigits(n);
    }

    cout << count << endl;

    puts("");
}